export interface NodeMethods {
  getAccount(): Promise<ArrayBuffer>;
  getAllAccounts(): Promise<ArrayBuffer[]>;
  getBalance(
    address: ArrayBuffer | string,
    tag: 'pending' | 'latest' | 'earliest' | BigNumber | number,
  ): Promise<BigNumber>;
  getChainId(): Promise<BigNumber>;
  getGasPrice(): Promise<BigNumber>;
  estimateGas(
    transaction: any,
    tag: 'pending' | 'latest' | 'earliest' | BigNumber | number,
  ): Promise<BigNumber>;
  ethCall(
    transaction: any,
    tag: 'pending' | 'latest' | 'earliest' | BigNumber | number,
  ): Promise<ArrayBuffer>;
  sendTransaction(transaction: any): Promise<ArrayBuffer>;
  signMessage(message: ArrayBuffer | string): Promise<ArrayBuffer>;
}
