export function onTestNet(index: number) {
  if (index % 1 !== 0 || index < 0) {
    throw new TypeError(`index<${index}> is invalid`);
  }

  return `m/44'/61'/0'/${index}`;
}

export function onMainNet(index: number) {
  if (index % 1 !== 0 || index < 0) {
    throw new TypeError(`index<${index}> is invalid`);
  }

  return `m/44'/60'/0'/${index}`;
}

export function isValid(v: string) {
  return /^(?:m\/)?44'\/6[0|1]'\/0'\/\d+$/.test(v);
}
