import {inBrowser} from '@browseth/utils';
import TransportNodeHID from '@ledgerhq/hw-transport-node-hid';
import TransportU2F from '@ledgerhq/hw-transport-u2f';

const Transport = inBrowser ? TransportU2F : TransportNodeHID;

// TODO(bp): maybe make different initializations for each transport
export function getTransport(): Promise<any> {
  return Transport.create();
}
