// TODO(bp): make the typings better
declare module 'bn.js' {
  import {Buffer} from 'buffer';

  type Endianness = 'le' | 'be';

  export default class BN {
    constructor(
      number: number | string | number[] | Buffer,
      base?: number,
      endian?: Endianness,
    );
    clone(): BN;
    toString(base?: number, length?: number): string;
    toNumber(): number;
    toJSON(): string;
    toArray(endian?: Endianness, length?: number): number[];
    toBuffer(endian?: Endianness, length?: number): Buffer;
    bitLength(): number;
    zeroBits(): number;
    byteLength(): number;
    isNeg(): boolean;
    isEven(): boolean;
    isZero(): boolean;
    cmp(b: any): number;
    eq(b: any): boolean;
    isBN(b: any): boolean;
    neg(): BN;
    abs(): BN;
    add(b: BN): BN;
    sub(b: BN): BN;
    mul(b: BN): BN;
    sqr(): BN;
    pow(b: BN): BN;
    div(b: BN): BN;
    mod(b: BN): BN;
    divRound(b: BN): BN;
    toTwos(width: number): BN;
    fromTwos(width: number): BN;
  }
}
